function renderBarChart(labels, data, elementId) {
    const ctx = document.getElementById(elementId).getContext('2d');
    new Chart(ctx, {
        type: 'bar',
        data: {
            labels: labels,
            datasets: [{
                label: 'AGI Milestone Progress',
                data: data,
                backgroundColor: 'rgba(75, 192, 192, 0.6)'
            }]
        }
    });
}

