{"language":"javascript","code":"async function reportTrafficData(sensorId, vehicleCount, averageSpeed) {
  const apiEndpoint = 'https://api.smartcity.gov.in/v1/traffic/update';
  const data = {
    sensorId: sensorId,
    vehicleCount: vehicleCount,
    averageSpeed: averageSpeed,
    timestamp: new Date().toISOString()
  };

  try {
    const response = await fetch(apiEndpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer YOUR_API_KEY'
      },
      body: JSON.stringify(data)
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const result = await response.json();
    console.log('Data reported successfully:', result);
  } catch (error) {
    console.error('Failed to report traffic data:', error);
  }
}

// Example usage:
reportTrafficData('SN-08-KORAMANGALA', 78, 15);"}
