async function setLightState(lightId, isOn, brightness) {
    const API_ENDPOINT = `https://api.yourhome.com/v1/lights/${lightId}/state`;
    const ACCESS_TOKEN = 'your_super_secret_auth_token';

    const body = {
        on: isOn,
        brightness: brightness // A value between 0 and 100
    };

    try {
        const response = await fetch(API_ENDPOINT, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${ACCESS_TOKEN}`
            },
            body: JSON.stringify(body)
        });

        if (!response.ok) {
            throw new Error(`API Error: ${response.status} ${response.statusText}`);
        }

        const result = await response.json();
        console.log('Light state updated successfully:', result);
        return result;

    } catch (error) {
        console.error('Failed to update light state:', error);
    }
}

// Usage: Turn light '1' on to 75% brightness
setLightState('1', true, 75);
