import re

# Define intents with associated keywords
INTENTS = {
    'track_order': [r'track', r'order status', r'where is my package'],
    'reset_password': [r'reset', r'password', r'forgot', r'can\'t login'],
    'contact_support': [r'talk to human', r'agent', r'support', r'contact']
}

def recognize_intent(user_query):
    """A simple function to recognize user intent based on keywords."""
    user_query = user_query.lower()
    for intent, keywords in INTENTS.items():
        for keyword in keywords:
            if re.search(keyword, user_query):
                return {'intent': intent, 'confidence': 0.9}
    return {'intent': 'unknown', 'confidence': 0.0}

# Example usage
query = "I need to reset my password."
print(f"Query: '{query}' -> Recognized Intent: {recognize_intent(query)}")

query2 = "What is the status of my order?"
print(f"Query: '{query2}' -> Recognized Intent: {recognize_intent(query2)}")

```
