const robotApiEndpoint = new URL('https://api.factory-floor.com/v2/robot/KUKA-KR6-R900/status');
const params = {
  'data_points': ['joint_angles', 'tcp_position', 'motor_temp', 'uptime'],
  'refresh_rate_ms': 500,
  'auth_token': 'secure-token-xyz-123'
};
Object.keys(params).forEach(key => robotApiEndpoint.searchParams.append(key, JSON.stringify(params[key])));

// This URL would be used to fetch real-time data for a monitoring dashboard.
console.log('Fetching data from:', robotApiEndpoint.href);
